package loops_injava;

public class Odd_EvenNumbers_DescendingOrder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  for (int i=100;i>=1;i--) {
	  if(i%2==0) {
		  System.out.println(i-1);
	  }
  }
  for(short j=100;j>=1;j--) {
	  if(j%2==0) {
	  System.out.println(j);
  }
  }
	}

}
